mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM (
SELECT * FROM MEMBER

	PASSWORD IS NOT NULL AND USER_ID != 'Admin'
	
		AND USER_ID = #{searchKeyword}
	
	
		AND NAME LIKE '%' || #{searchKeyword} || '%'
	
	
		AND EMAIL LIKE '%' || #{searchKeyword} || '%'
	
	
		AND PHONE_NUM LIKE '%' || #{searchKeyword} || '%'
	

) A )WHERE RNUM BETWEEN #{start} AND #{end}

mapper.update:
UPDATE MEMBER SET PASSWORD = null, NAME = null, ADDRESS = null, PHONE_NUM = null, EMAIL = null, INTRODUCTION = null, POSTCODE = null, ADDR_DETAIL = null
USER_ID IN
#{item}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT PROJECT.PROJECT_NO, PROJECT.PROJECT_NAME, MEMBER.NAME, PROJECT.USER_ID, PROJECT.SUPPORT_PICK_YN, COMMON_CODE.CODE_NAME PROJECT_STATUS FROM PROJECT JOIN MEMBER ON PROJECT.USER_ID = MEMBER.USER_ID JOIN COMMON_CODE ON PROJECT.PROGRESS = COMMON_CODE.CODE AND COMMON_CODE.CODE_GROUP='C03'
PASSWORD IS NOT NULL
AND PROJECT.PROJECT_NO LIKE '%' || #{searchKeyword} || '%'
AND PROJECT.PROJECT_NAME LIKE '%' || #{searchKeyword} || '%'
AND MEMBER.NAME LIKE '%' || #{searchKeyword} || '%'
AND PROJECT.USER_ID LIKE '%' || #{searchKeyword} || '%'
AND PROJECT.SUPPORT_PICK_YN = #{supportForMePickUp}
AND PROJECT.PROGRESS ='001'
) A )WHERE RNUM BETWEEN #{start} AND #{end}
