mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT INVEST.INVEST_NO AS INVEST_NO, PROJECT.PROJECT_NAME AS PROJECT_NAME, INVEST.INVESTMENT_AMOUNT AS INVENST_AMOUNT, COMMON_CODE.CODE_NAME AS PAY_TYPE, COMMON_CODE2.CODE_NAME AS PAYMENT_STATE, TO_DATE(SUBSTR(INVEST.INVEST_NO,1,8),'rrrr-mm-dd') AS INVEST_DATE FROM INVEST JOIN PROJECT ON INVEST.PROJECT_NO = PROJECT.PROJECT_NO JOIN COMMON_CODE ON (COMMON_CODE.CODE = INVEST.PAY_TYPE AND COMMON_CODE.CODE_GROUP ='A01') JOIN COMMON_CODE COMMON_CODE2 ON (COMMON_CODE2.CODE = INVEST.PAYMENT_STATE AND COMMON_CODE2.CODE_GROUP ='B02')
INVEST.USER_ID = #{userId}
AND PROJECT.PROJECT_NAME LIKE '%' || #{searchKeyword} || '%'
AND INVEST.INVEST_NO LIKE '%' || #{searchKeyword} || '%'
AND TO_DATE(SUBSTR(INVEST.INVEST_NO,1,8),'rrrr-mm-dd') BETWEEN #{startdate} AND #{enddate}
AND INVEST.PAY_TYPE = '001'
AND INVEST.PAY_TYPE = '002'
AND INVEST.PAY_TYPE = '003'
ORDER BY INVEST.INVEST_NO DESC ) A )WHERE RNUM BETWEEN #{start} AND #{end}
