mapper.select:
	SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM (
		SELECT * FROM MEMBER 
		
			
				id in
				
					#{item}
				
			
			
				AND USER_ID '%' || #{searchKeyword} || '%'
			
			
				AND NAME LIKE '%' || #{searchKeyword} || '%'
			

				
		 ) A )WHERE RNUM BETWEEN #{start} AND #{end}

SELECT PASSWORD 
FROM MEMBER
WHERE  USER_ID = #{userId}

SELECT COUNT(USER_ID) 
FROM MEMBER
WHERE  USER_ID = #{userId}
