mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM (
	SELECT * FROM USERS 
	
		
			id in
			
				#{item}
			
		
		
			and id like '%' || #{searchKeyword} || '%'
		
		
			and name like '%' || #{searchKeyword} || '%'
		
		
			and role  like '%' || #{role} || '%'
		
	
			
	 		ORDER BY ${sort}
		
			
	 		ORDER BY ID
		
	
	 ) A )WHERE RNUM BETWEEN #{start} AND #{end}

mapper.update:
UPDATE USERS 
SET 
PASSWORD = #{password}, 
NAME = #{name}, 
ROLE = #{role} 
WHERE  ID = #{id}
